mapper.update:
update camping_reviews
set user_idx = #{user_idx}, rating = #{rating}, title = #{title}, content = #{content}, created_at = NOW(), filename = #{filename}
where review_idx = #{review_idx}

mapper.insert:
INSERT INTO camping_reviews (
    contentId, user_idx, username, rating, title, content, filename, created_at
) VALUES (
    #{contentId}, #{user_idx}, #{username}, #{rating}, #{title}, #{content}, #{filename}, NOW()
)
